INSERT INTO `qs_course` VALUES ('1526','0','69737','汽车估损师高级职业资格认证考试','2','徐州市银地东大职业培训学校','29','16','汽车培训','1881','白天班','0','','11','202','江苏省/徐州市','各车辆保险公司定损人员，公估公司专业公估人员，二手车鉴定评估人员，资产评估机构评估人员，维修企业接待','人保部（原劳动和社会保障部）职业技能鉴定中心授予全国统一的《','80','0','0','培训与考核方式\r\n      采用周末或脱产形式培训，时间不少于80课时，学员要参加系统培训和统一考核。考核内容依据中华人民共和国劳动和社会保障部鉴定中心制定的认证标准，及标准所规定的知识和技能要求具体确定，考试时间定于2015年3月、5月、6月、8月、10月、12月中下旬。由人保部（原劳动和社会保障部）职业技能鉴定中心授予全国统一的《汽车估损师高级职业资格》，并颁发认证OSTA证书。具体报名事宜详询东大职业学校。\r\n报名条件\r\n1、取得中等职业学校汽车相关专业毕业证书或同等以上学历，并连续从事两年以上汽车碰撞修复或估损工作。\r\n2、取得汽车相关专业本科或同等以上学历的人员。\r\n（符合以上条件之一者均可报名）\r\n六、 学习内容：\r\nA.车辆保险法律常识及应用知识\r\nB.车辆识别与估损资料使用\r\nC.汽车结构及性能\r\nD.车辆碰撞损伤的类型及损伤鉴定；\r\nE. 事故车辆查勘技能\r\nF. 事故车估损定损\r\nG. 事故车的全损、残值处理\r\nH. 机动车辆保险理赔的基础知识和审批流程\r\nI.撰写机动车辆保险定损报告\r\nJ.经典事故与保险理赔案例研讨。\r\n七、报名要求\r\n报名时填写汽车估损师（高级）考生报名审批表一式二份\r\n同时提交以下证件：\r\n1、身份证复印件2张；；\r\n2、学历证书复印件2张； \r\n4、1寸2张、1.5寸免冠蓝底照片4张。\r\n八、证书颁发与作用\r\n鉴定合格者，可获得由人力资源和社会保障部职业技能鉴定中心颁发的《中华人民共和国汽车估损师OSTA证书》。该证书是国家直接出具的从业者上岗和任职的职业资格证明，全国联网查询、全国通用。','0','1439435676','1465355676','1439435676','0','0','','1','1','2','汽车估损师高级职业资格认证考试 徐州市银地东大职业培训学校  人保部（原劳动和社会保障部）职业技能鉴定中心授予全国统一的《汽车估损师高级职业资格》，并颁发认证OSTA证书 汽车00 高级00 职业00 资格00 认证00 考试00 徐州00 州市00 东大00 培训00 学校00 人保00 劳动00 社会00 保障00 技能00 鉴定00 中心00 授予00 全国00 统一00 颁发00 证书00 江苏00 江苏省0 考核00 方式00 采用00 周末00 脱产00 形式00 学员00 参加00 系统00 内容00 依据00 中华00 华人00 人民00 共和00 制定00 标准00 规定00 知识00 要求00 具体00 确定00 月中00 中下00 中下旬0 下旬00 报名00 事宜00 条件00 取得00 中等00 相关00 专业00 毕业00 毕业证0 同等00 以上00 上学00 学历00 连续00 从事00 上汽00 碰撞00 修复00 工作00 本科00 人员00 符合00 之一00 学习00 车辆00 保险00 保险法0 法律00 常识00 应用00 识别00 资料00 使用00 结构00 性能00 损伤00 类型00 事故00 查勘00 处理00 机动00 机动车0 动车00 保险理赔 险理赔0 理赔00 基础00 审批00 流程00 撰写00 报告00 经典00 赔案00 案例00 研讨00 填写00 考生00 审批表0 同时00 提交00 以下00 证件00 身份00 身份证0 复印00 复印件0 学历证0 免冠00 照片00 作用00 合格00 合格者0 获得00 人力00 人力资源 资源00 国家00 直接00 出具00 从业00 从业者0 业者00 上岗00 任职00 资格证0 证明00 联网00 查询00 通用00','汽车估损师高级职业资格认证考试,徐州市银地东大职业培训学校,,人保部（原劳动和社会保障部）职业技能鉴定中心授予全国统一的《汽车估损师高级职业资格》，并颁发认证OSTA证书','','0.000000','0.000000','1','1','0');
