INSERT INTO `qs_course` VALUES ('1471','0','69872','汽车维修高级班','2','重庆市大园职业培训学校','144','16','汽车培训','1880','随到随学','0','','4','96','重庆市/九龙坡区','初高中毕业生  15岁以上喜爱汽车维修的社会人员','汽车职业资格证书','14个月','18800','0','汽车维修高级班\r\n \r\n（入学签就业合同保证就业，学员创业免费提供技术支持，一期不会，下期免费学习，保证学会为止）。\r\n \r\n1.送床上用品\r\n \r\n2.送相关专业全套教材\r\n \r\n3.送创业开店方案，扶持新店开张及永久技术支持\r\n \r\n4.送价值3000元的500G汽车维修美容专业内部资料\r\n \r\n5.所有学员入学签订合同，保障学会，毕业分配\r\n \r\n学时 :14个月\r\n \r\n学费:18800\r\n \r\n汽车维修高级班\r\n \r\n职业规划\r\n \r\n通过本专业学习：成为汽车机电方面的专业技术型人才\r\n \r\n入学条件\r\n \r\n1、初中、高中或同等学历的应届、往届毕业生　\r\n \r\n2、具有良好的学习能力和动手能力\r\n \r\n3、有志于从事汽车行业\r\n \r\n4、对汽车不了解的驾驶人员，想简单处理汽车小故障的学员　\r\n \r\n学习内容\r\n \r\n1、汽车发动机原理及构造，发动机拆解、组装、维护保养及零部件更换\r\n \r\n2、汽车底盘原理及构造，底盘拆解、组装、维护保养及零部件更换\r\n \r\n3、汽车常规保养\r\n \r\n4、汽车电器构造与维修\r\n \r\n5、电控发动机的原理与检修，汽车电器专项培训，多车系电路检修。\r\n \r\n6、高级电子控制发动机的原理、构造与维修\r\n \r\n7、电子巡航控制、电子控制进气系统、电子控制燃油喷射系统、空调电子控制系统等原理、构造与维修\r\n \r\n8、底盘电子控制，自动变速器，安全气囊，电控悬架技术\r\n \r\n9、专项培训美系，德系，日系等V型6缸发动机、涡轮增压发动机、直喷发动机等高技术性能发动机的原理、拆解、组装与维修\r\n \r\n10、专项培训多种高级电控汽车检测设备的使用及对整车的故障诊断与排除','0','1439263033','1441855033','1439263033','0','0','','1','1','1','汽车维修高级班 重庆市大园职业培训学校  汽车职业资格证书 汽车00 汽车维修 维修00 高级00 高级班0 重庆00 职业00 培训00 学校00 资格00 资格证0 证书00 九龙00 入学00 就业00 合同00 保证00 学员00 创业00 免费00 提供00 技术00 技术支持 支持00 一期00 不会00 下期00 学习00 学会00 为止00 床上00 用品00 相关00 专业00 全套00 教材00 开店00 店方00 方案00 扶持00 新店00 开张00 永久00 所有00 签订00 保障00 毕业00 分配00 学时00 个月00 学费00 规划00 通过00 成为00 为汽车0 机电00 方面00 面的00 技术型0 人才00 条件00 初中00 高中00 同等00 学历00 应届00 往届00 毕业生0 具有00 良好00 能力00 动手00 有志00 有志于0 从事00 车行00 行业00 不了00 了解00 驾驶00 驾驶人0 人员00 简单00 处理00 故障00 内容00 发动00 发动机0 动机00 原理00 构造00 拆解00 组装00 维护00 保养00 零部件0 部件00 更换00 车底00 底盘00 常规00 电器00 检修00 专项00 车系00 电路00 路检00 电子00 控制00 制发00 巡航00 进气00 系统00 燃油00 喷射00 空调00 自动00 变速00 变速器0 安全00 气囊00 涡轮00 喷发00 等高00 高技术0 技术性0 性能00 多种00 检测00 设备00 使用00 整车00 诊断00 排除00','汽车维修高级班,重庆市大园职业培训学校,,汽车职业资格证书','','0.000000','0.000000','1','1','0');
